import React from "react";
import { Container, Row, Col, Card } from "react-bootstrap";
import { motion } from "framer-motion";

function AboutPage() {
    return (
        <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            exit={{ opacity: 0, y: -20 }}
            transition={{ duration: 0.5 }}
        >
            <Container className="py-5">
                {/* Header Section */}
                <Row className="text-center mb-5">
                    <Col>
                        <h1 className="display-4 fw-bold">About CommunionHub</h1>
                        <p className="lead text-muted">
                            Connecting people across faiths through events and shared experiences.
                        </p>
                    </Col>
                </Row>

                {/* Features Section */}
                <Row className="g-4">
                    {/* Feature 1 */}
                    <Col md={4}>
                        <motion.div
                            whileHover={{ scale: 1.05 }}
                            whileTap={{ scale: 0.95 }}
                        >
                            <Card className="h-100 shadow-sm">
                                <Card.Body>
                                    <Card.Title className="text-primary">Our Mission</Card.Title>
                                    <Card.Text>
                                        To bridge communities by enabling connections through shared events
                                        and interests.
                                    </Card.Text>
                                </Card.Body>
                            </Card>
                        </motion.div>
                    </Col>

                    {/* Feature 2 */}
                    <Col md={4}>
                        <motion.div
                            whileHover={{ scale: 1.05 }}
                            whileTap={{ scale: 0.95 }}
                        >
                            <Card className="h-100 shadow-sm">
                                <Card.Body>
                                    <Card.Title className="text-primary">What We Offer</Card.Title>
                                    <Card.Text>
                                        A platform to discover events, connect with like-minded individuals, 
                                        and build stronger communities.
                                    </Card.Text>
                                </Card.Body>
                            </Card>
                        </motion.div>
                    </Col>

                    {/* Feature 3 */}
                    <Col md={4}>
                        <motion.div
                            whileHover={{ scale: 1.05 }}
                            whileTap={{ scale: 0.95 }}
                        >
                            <Card className="h-100 shadow-sm">
                                <Card.Body>
                                    <Card.Title className="text-primary">Why Join Us?</Card.Title>
                                    <Card.Text>
                                        Experience seamless event management, real-time updates, and a vibrant 
                                        community all in one place.
                                    </Card.Text>
                                </Card.Body>
                            </Card>
                        </motion.div>
                    </Col>
                </Row>
            </Container>
        </motion.div>
    );
}

export default AboutPage;
